package com.aw.userprofile.domain;

public class UserTagsCountOutputDomain {
	
	private String userId;
	
	private String tag;
	
	private Integer count;

	public UserTagsCountOutputDomain() {
		super();
		// TODO Auto-generated constructor stub
	}

	public UserTagsCountOutputDomain(String userId, String tag, Integer count) {
		super();
		this.userId = userId;
		this.tag = tag;
		this.count = count;
	}

	public String getUserId() {
		return userId;
	}

	public void setUserId(String userId) {
		this.userId = userId;
	}

	public String getTag() {
		return tag;
	}

	public void setTag(String tag) {
		this.tag = tag;
	}

	public Integer getCount() {
		return count;
	}

	public void setCount(Integer count) {
		this.count = count;
	}

	@Override
	public int hashCode() {
		final int prime = 31;
		int result = 1;
		result = prime * result + ((count == null) ? 0 : count.hashCode());
		result = prime * result + ((tag == null) ? 0 : tag.hashCode());
		result = prime * result + ((userId == null) ? 0 : userId.hashCode());
		return result;
	}

	@Override
	public boolean equals(Object obj) {
		if (this == obj)
			return true;
		if (obj == null)
			return false;
		if (getClass() != obj.getClass())
			return false;
		UserTagsCountOutputDomain other = (UserTagsCountOutputDomain) obj;
		if (count == null) {
			if (other.count != null)
				return false;
		} else if (!count.equals(other.count))
			return false;
		if (tag == null) {
			if (other.tag != null)
				return false;
		} else if (!tag.equals(other.tag))
			return false;
		if (userId == null) {
			if (other.userId != null)
				return false;
		} else if (!userId.equals(other.userId))
			return false;
		return true;
	}

	@Override
	public String toString() {
		return "UserTagsCountOutputDomain [userId=" + userId + ", tag=" + tag + ", count=" + count + "]";
	}

}
